import React, { useEffect, useState } from "react";

export default function TopPicks() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/admin")
      .then((res) => res.json())
      .then((data) => setProducts(Array.isArray(data) ? data.slice(0, 3) : []))
      .catch((err) =>
        console.error("❌ Грешка при зареждане на продукти:", err)
      );
  }, []);

  return (
    <section id="top-picks" className="bg-white py-16">
      <div className="max-w-6xl mx-auto px-4 text-center">
        <h2 className="text-2xl font-bold text-blue-800 mb-2">Top Antivirus Picks</h2>
        <p className="text-gray-600 text-sm mb-10">
          Our recommended antivirus software based on performance, protection, and value.
        </p>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {products.map((product) => (
            <div key={product.ProductID} className="bg-gray-100 p-6 rounded-lg shadow hover:shadow-md transition">
              <img
                src={product.ImageURL}
                alt={product.ProductName}
                className="w-16 h-16 mx-auto object-contain mb-4"
              />
              <h3 className="text-lg font-semibold mb-2">{product.ProductName}</h3>
              <p className="text-sm text-gray-700 mb-2">
                {product.Description?.slice(0, 100)}...
              </p>
              <div className="text-blue-600 font-semibold mb-2">
                {product.Price}
              </div>
              <button
                onClick={() => window.open(product.OfficialWebsite, "_blank")}
                className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 text-sm"
              >
                Visit Website
              </button>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
