import React, { useState } from "react";

function Register() {
  const [fullName, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const handleRegister = async (e) => {
    e.preventDefault();
    const res = await fetch("http://localhost:5000/api/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ fullName, email, password, confirmPassword }),
    });
    const data = await res.json();
    alert(data.message || "Успешна регистрация!");
  };

  return (
    <div className="form-container">
      <h2>Регистрация</h2>
      <form onSubmit={handleRegister}>
        <input type="text" placeholder="Име" onChange={(e) => setFullName(e.target.value)} required />
        <input type="email" placeholder="Имейл" onChange={(e) => setEmail(e.target.value)} required />
        <input type="password" placeholder="Парола" onChange={(e) => setPassword(e.target.value)} required />
        <input type="password" placeholder="Потвърди паролата" onChange={(e) => setConfirmPassword(e.target.value)} required />
        <button type="submit">Регистрация</button>
      </form>
      <a href="/">Вече имате акаунт? Влезте</a>
    </div>
  );
}

export default Register;
