// ✅ Обновен AdminPageTemp.js с Accordion стил и данни от твоето текущо състояние
import React, { useState, useEffect } from "react";
import Navbar from "./components/Navbar";
import "./index.css";

function AdminPageTemp() {
  const [products, setProducts] = useState([]);
  const [editingId, setEditingId] = useState(null);

  // Основна информация
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [image, setImage] = useState("");
  const [fileName, setFileName] = useState("");

  // Key Features
  const [features, setFeatures] = useState("");

  // Expert Review
  const [ourReview, setOurReview] = useState("");
  const [pros, setPros] = useState("");
  const [cons, setCons] = useState("");
  const [bottomLine, setBottomLine] = useState("");

  // Test Results
  const [malware, setMalware] = useState("");
  const [performance, setPerformance] = useState("");
  const [ui, setUI] = useState("");
  const [value, setValue] = useState("");

  // Protection Features
  const [protectionFeatures, setProtectionFeatures] = useState("");

  const [openSection, setOpenSection] = useState(null);

  const toggleSection = (section) => {
    setOpenSection(openSection === section ? null : section);
  };

  useEffect(() => {
    fetch("http://localhost:5000/api/admin")
      .then((res) => res.json())
      .then((data) => setProducts(Array.isArray(data) ? data : []))
      .catch((err) => console.error("❌ Грешка при зареждане:", err));
  }, []);

  const convertToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });
  };

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const base64 = await convertToBase64(file);
      setImage(base64);
      setFileName(file.name);
    }
  };

  const handleAdd = async () => {
    if (!name.trim() || !description.trim() || !price.trim() || !image.trim()) {
      alert("Моля, попълнете всички полета.");
      return;
    }

    const method = editingId ? "PUT" : "POST";
    const url = editingId
      ? `http://localhost:5000/api/admin/${editingId}`
      : "http://localhost:5000/api/admin";

    try {
      const res = await fetch(url, {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, description, image, price })
      });

      const text = await res.text();
      let data;
      try {
        data = JSON.parse(text);
      } catch {
        alert("Сървърът върна неочакван отговор.");
        return;
      }

      if (res.ok) {
        alert(data.message);
        setName("");
        setDescription("");
        setPrice("");
        setImage("");
        setFileName("");
        setEditingId(null);
        fetchProducts();
      } else {
        alert("Грешка при заявката");
      }
    } catch (err) {
      console.error("❌ Грешка при заявката:", err);
    }
  };

  return (
    <div className="admin-page">
      <Navbar />
      <div className="p-6 max-w-4xl mx-auto space-y-6">
        <h2 className="text-2xl font-bold text-center text-white">Админ Панел</h2>

        {/* Accordion секции */}
        {[
          {
            title: "Основна информация",
            content: (
              <div className="space-y-2">
                <input className="input" placeholder="Име" value={name} onChange={(e) => setName(e.target.value)} />
                <input className="input" placeholder="Описание" value={description} onChange={(e) => setDescription(e.target.value)} />
                <input className="input" placeholder="Цена" value={price} onChange={(e) => setPrice(e.target.value)} />
                <label htmlFor="fileUpload" className="custom-file-upload">Изображение</label>
                <input id="fileUpload" type="file" accept="image/*" onChange={handleImageChange} style={{ display: "none" }} />
                {fileName && <p>Избран файл: {fileName}</p>}
                <button onClick={handleAdd} className="btn-blue">{editingId ? "Запази" : "Добави"}</button>
              </div>
            )
          },
          {
            title: "Key Features",
            content: (
              <div>
                <textarea className="textarea" value={features} onChange={(e) => setFeatures(e.target.value)} />
                <button className="btn-blue mt-2">Запиши Key Features</button>
              </div>
            )
          },
          {
            title: "Expert Review",
            content: (
              <div className="space-y-2">
                <textarea className="textarea" placeholder="Our Review" value={ourReview} onChange={(e) => setOurReview(e.target.value)} />
                <textarea className="textarea" placeholder="Pros" value={pros} onChange={(e) => setPros(e.target.value)} />
                <textarea className="textarea" placeholder="Cons" value={cons} onChange={(e) => setCons(e.target.value)} />
                <textarea className="textarea" placeholder="Bottom Line" value={bottomLine} onChange={(e) => setBottomLine(e.target.value)} />
                <button className="btn-blue">Запиши Expert Review</button>
              </div>
            )
          },
          {
            title: "Test Results",
            content: (
              <div className="grid grid-cols-2 gap-2">
                <input className="input" placeholder="Malware Protection" value={malware} onChange={(e) => setMalware(e.target.value)} />
                <input className="input" placeholder="Performance Impact" value={performance} onChange={(e) => setPerformance(e.target.value)} />
                <input className="input" placeholder="User Interface" value={ui} onChange={(e) => setUI(e.target.value)} />
                <input className="input" placeholder="Value for Money" value={value} onChange={(e) => setValue(e.target.value)} />
                <button className="btn-blue col-span-2">Запиши Test Results</button>
              </div>
            )
          },
          {
            title: "Protection Features",
            content: (
              <div>
                <textarea className="textarea" placeholder="Protection features, всяка на нов ред" value={protectionFeatures} onChange={(e) => setProtectionFeatures(e.target.value)} />
                <button className="btn-blue mt-2">Запиши Protection Features</button>
              </div>
            )
          }
        ].map((section, idx) => (
          <div key={idx} className="accordion bg-white rounded shadow-md">
            <button className="w-full text-left p-4 font-semibold" onClick={() => toggleSection(idx)}>{section.title}</button>
            {openSection === idx && (
              <div className="p-4 border-t">{section.content}</div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default AdminPageTemp;
