import React, { useEffect } from "react";
import "./index.css";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Login from "./Login";
import Register from "./Register";
import AdminPage from "./AdminPageT";
import ProtectedRoute from "./ProtectedRoute";
import Navbar from "./components/Navbar";
import Hero from "./components/Hero";
import AntivirusList from "./components/AntivirusListT";
import ProductDetails from "./ProductDetails";
import ComparePage from "./ComparePage";
import FAQSection from "./components/FAQSection";
import StayProtected from "./components/StayProtected";
import Footer from "./components/Footer";
import GdprPolicy from ".components/GdprPolicy"; // ✅ GDPR компонент

import {
  TermsOfService,
  PrivacyPolicy,
  CookiePolicy,
  AffiliateDisclosure,
  TestingMethodology,
} from "./LegalPages";

function App() {
  useEffect(() => {
    localStorage.removeItem("token");
    localStorage.removeItem("isAdmin");
  }, []);

  return (
    <div className="min-h-screen flex flex-col">
      <Router>
        <Navbar />

        <div className="flex-grow">
          <Routes>
            {/* Начална страница */}
            <Route
              path="/"
              element={
                <>
                  <Hero />
                  <AntivirusList />
                  <FAQSection />
                  <StayProtected />
                </>
              }
            />

            {/* Страници */}
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/compare" element={<ComparePage />} />
            <Route
              path="/admin"
              element={
                <ProtectedRoute>
                  <AdminPage />
                </ProtectedRoute>
              }
            />
            <Route path="/product/:name" element={<ProductDetails />} />

            {/* Legal Pages */}
            <Route path="/terms" element={<TermsOfService />} />
            <Route path="/privacy" element={<PrivacyPolicy />} />
            <Route path="/cookies" element={<CookiePolicy />} />
            <Route path="/affiliate" element={<AffiliateDisclosure />} />
            <Route path="/testing" element={<TestingMethodology />} />
            <Route path="/gdpr" element={<GdprPolicy />} /> {/* ✅ GDPR */}
          </Routes>
        </div>

        <Footer />
      </Router>
    </div>
  );
}

export default App;
