import React, { useState, useEffect } from "react";
import Navbar from "./components/Navbar";
import "./index.css";

function AdminPageTemp() {
  const [products, setProducts] = useState([]);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState("");
  const [fileName, setFileName] = useState("");

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin/products");
      const data = await res.json();

      console.log("✅ Данни от сървъра:", data);

      if (Array.isArray(data)) {
        setProducts(data);
      } else {
        console.warn("⚠️ Отговорът не е масив:", data);
        setProducts([]);
      }
    } catch (err) {
      console.error("❌ Грешка при зареждане:", err);
      setProducts([]);
    }
  };

  const convertToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });
  };

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const base64 = await convertToBase64(file);
      setImage(base64);
      setFileName(file.name);
    }
  };

  const handleAdd = async () => {
    if (!name || !description || !image) {
      alert("Моля, попълнете всички полета и добавете изображение.");
      return;
    }

    try {
      const res = await fetch("http://localhost:5000/api/admin/products", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          name,
          description,
          image,
        }),
      });
      const data = await res.json();
      alert(data.message || "Добавен продукт!");
      setName("");
      setDescription("");
      setImage("");
      setFileName("");
      fetchProducts();
    } catch (err) {
      console.error("Грешка при добавяне:", err);
    }
  };

  const handleDelete = async (id) => {
    try {
      const res = await fetch(
        `http://localhost:5000/api/admin/products/${id}`,
        {
          method: "DELETE",
        }
      );
      const data = await res.json();
      alert(data.message || "Изтрит продукт!");
      fetchProducts();
    } catch (err) {
      console.error("Грешка при изтриване:", err);
    }
  };

  return (
    <div className="admin-page">
      <Navbar />
      <div className="admin-form">
        <h2>Добавяне на продукт</h2>
        <input
          type="text"
          placeholder="Име"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Описание"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />

        <label htmlFor="fileUpload" className="custom-file-upload">
          Изображение
        </label>
        <input
          id="fileUpload"
          type="file"
          accept="image/*"
          onChange={handleImageChange}
          style={{ display: "none" }}
        />
        {fileName && (
          <p style={{ marginTop: "5px", fontSize: "14px" }}>
            Избран файл: {fileName}
          </p>
        )}

        <button onClick={handleAdd}>Добави</button>
      </div>

      <div className="product-table">
        <table>
          <thead>
            <tr>
              <th>Снимка</th>
              <th>Име</th>
              <th>Описание</th>
              <th>Действие</th>
            </tr>
          </thead>
          <tbody>
            {!Array.isArray(products) || products.length === 0 ? (
              <tr>
                <td colSpan="4">Няма продукти.</td>
              </tr>
            ) : (
              products.map((p) => (
                <tr key={p.ProductID}>
                  <td>
                    {p.ImageURL && (
                      <img
                        src={p.ImageURL}
                        alt={p.ProductName}
                        style={{ width: 50 }}
                      />
                    )}
                  </td>
                  <td>{p.ProductName}</td>
                  <td>{p.Description}</td>
                  <td>
                    <button
                      onClick={() => handleDelete(p.ProductID)}
                      className="delete-button"
                    >
                      Изтрий
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AdminPageTemp;
