import React, { useEffect, useState } from "react";
import Navbar from "./components/Navbar";
import "./index.css";

function AdminPageT() {
  const [products, setProducts] = useState([]);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [image, setImage] = useState("");
  const [fileName, setFileName] = useState("");
  const [features, setFeatures] = useState("");
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin");
      const data = await res.json();
      setProducts(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞—Ä–µ–∂–¥–∞–Ω–µ:", err);
    }
  };

  const convertToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });
  };

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const base64 = await convertToBase64(file);
      setImage(base64);
      setFileName(file.name);
    }
  };

  const handleAdd = async () => {
    if (!name.trim() || !description.trim() || !price.trim() || !image.trim()) {
      alert("–ú–æ–ª—è, –ø–æ–ø—ä–ª–Ω–µ—Ç–µ –≤—Å–∏—á–∫–∏ –ø–æ–ª–µ—Ç–∞.");
      return;
    }

    const method = editingId ? "PUT" : "POST";
    const url = editingId
      ? `http://localhost:5000/api/admin/${editingId}`
      : "http://localhost:5000/api/admin";

    try {
      const res = await fetch(url, {
        method,
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, description, image, price }),
      });

      const text = await res.text();
      let data;
      try {
        data = JSON.parse(text);
      } catch {
        console.error("‚ùå –û—Ç–≥–æ–≤–æ—Ä—ä—Ç –Ω–µ –µ –≤–∞–ª–∏–¥–µ–Ω JSON:", text);
        alert("–°—ä—Ä–≤—ä—Ä—ä—Ç –≤—ä—Ä–Ω–∞ –Ω–µ–æ—á–∞–∫–≤–∞–Ω –æ—Ç–≥–æ–≤–æ—Ä.");
        return;
      }

      if (res.ok) {
        alert(data.message);
        setName("");
        setDescription("");
        setPrice("");
        setImage("");
        setFileName("");
        setFeatures("");
        setEditingId(null);
        fetchProducts();
      } else {
        console.log("‚ùå SERVER ERROR:", data);
        alert("–ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞—è–≤–∫–∞—Ç–∞");
      }
    } catch (err) {
      console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞—è–≤–∫–∞—Ç–∞:", err);
    }
  };

  const handleDelete = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/api/admin/${id}`, {
        method: "DELETE",
      });

      const data = await res.json();
      alert(data.message);
      fetchProducts();
    } catch (err) {
      console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∏–∑—Ç—Ä–∏–≤–∞–Ω–µ:", err);
    }
  };

  const handleFeatureSave = async () => {
    if (!editingId) return;
    try {
      const res = await fetch(`http://localhost:5000/api/admin/${editingId}/features`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({ features }),
      });

      const data = await res.json();
      alert(data.message);
      setFeatures("");
      setEditingId(null);
      fetchProducts();
    } catch (err) {
      console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞–ø–∏—Å–≤–∞–Ω–µ –Ω–∞ features:", err);
      alert("–ù–µ—É—Å–ø–µ—à–Ω–æ –∑–∞–ø–∏—Å–≤–∞–Ω–µ –Ω–∞ Key Features.");
    }
  };

  return (
    <div className="admin-page">
      <Navbar />
      <div className="admin-form">
        <h2>{editingId ? "–†–µ–¥–∞–∫—Ü–∏—è –Ω–∞ –ø—Ä–æ–¥—É–∫—Ç" : "–î–æ–±–∞–≤—è–Ω–µ –Ω–∞ –ø—Ä–æ–¥—É–∫—Ç"}</h2>
        <input type="text" placeholder="–ò–º–µ" value={name} onChange={(e) => setName(e.target.value)} />
        <input type="text" placeholder="–û–ø–∏—Å–∞–Ω–∏–µ" value={description} onChange={(e) => setDescription(e.target.value)} />
        <input type="text" placeholder="–¶–µ–Ω–∞" value={price} onChange={(e) => setPrice(e.target.value)} />
        <label htmlFor="fileUpload" className="custom-file-upload">–ò–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ</label>
        <input id="fileUpload" type="file" accept="image/*" onChange={handleImageChange} style={{ display: "none" }} />
        {fileName && <p>–ò–∑–±—Ä–∞–Ω —Ñ–∞–π–ª: {fileName}</p>}
        <button onClick={handleAdd}>{editingId ? "–ó–∞–ø–∞–∑–∏" : "–î–æ–±–∞–≤–∏"}</button>
      </div>

      {editingId && (
        <div className="features-section">
          <h3>–†–µ–¥–∞–∫—Ü–∏—è –Ω–∞ Key Features</h3>
          <textarea
            className="features-textarea"
            value={features}
            onChange={(e) => setFeatures(e.target.value)}
            placeholder="–í—ä–≤–µ–¥–∏ Key Features –ø–æ —Ä–µ–¥–æ–≤–µ..."
          />
          <button onClick={handleFeatureSave}>üíæ –ó–∞–ø–∏—à–∏ Key Features</button>
        </div>
      )}

      <div className="product-table">
        <table>
          <thead>
            <tr>
              <th>–°–Ω–∏–º–∫–∞</th>
              <th>–ò–º–µ</th>
              <th>–û–ø–∏—Å–∞–Ω–∏–µ</th>
              <th>–¶–µ–Ω–∞</th>
              <th>–î–µ–π—Å—Ç–≤–∏–µ</th>
            </tr>
          </thead>
          <tbody>
            {products.map((p) => (
              <tr key={p.ProductID}>
                <td><img src={p.ImageURL} alt={p.ProductName} style={{ width: 50 }} /></td>
                <td>{p.ProductName}</td>
                <td>{p.Description}</td>
                <td>{p.Price}</td>
                <td style={{ display: "flex", flexDirection: "column", gap: "0.5rem" }}>
                  <button
                    className="edit-button"
                    onClick={() => {
                      setEditingId(p.ProductID);
                      setName(p.ProductName);
                      setDescription(p.Description);
                      setPrice(p.Price);
                      setImage(p.ImageURL);
                      setFeatures(p.KeyFeatures || "");
                    }}
                  >
                    –†–µ–¥–∞–∫—Ç–∏—Ä–∞–π
                  </button>
                  <button className="delete-button" onClick={() => handleDelete(p.ProductID)}>
                    –ò–∑—Ç—Ä–∏–π
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AdminPageT;
