import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "./context/AuthContext";

function Login() {
  const [fullName, setFullName] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const { login } = useContext(AuthContext);

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!fullName || !password) {
      alert("Моля, въведете име и парола.");
      return;
    }

    try {
      const res = await fetch("http://localhost:5000/api/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ fullName, password }),
      });

      const data = await res.json();

      if (res.ok) {
        login(data.token, data.user.isAdmin);
        alert(data.message || "Успешен вход!");
        navigate("/");
      } else {
        alert(data.message || "Грешка при входа.");
      }
    } catch (err) {
      alert("Възникна грешка при връзката със сървъра.");
      console.error(err);
    }
  };

  return (
    // Същият JSX като досега...
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-purple-500 to-pink-500">
      <form onSubmit={handleLogin} className="bg-white shadow-xl rounded-lg p-8 w-full max-w-md animate-fade-in">
        <h2 className="text-2xl font-bold text-center mb-6 text-gray-800">Вход</h2>
        <input type="text" placeholder="Име" value={fullName} onChange={(e) => setFullName(e.target.value)} required className="w-full px-4 py-2 mb-4 border border-gray-300 rounded" />
        <input type="password" placeholder="Парола" value={password} onChange={(e) => setPassword(e.target.value)} required className="w-full px-4 py-2 mb-4 border border-gray-300 rounded" />
        <button type="submit" className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded">Вход</button>
        <p className="mt-4 text-center text-sm text-gray-600">
          Нямате акаунт?{" "}
          <a href="/register" className="text-green-600 hover:underline">Регистрирайте се</a>
        </p>
      </form>
    </div>
  );
}

export default Login;
