import React, { useEffect, useState } from "react";

function ReviewSection({ productId }) {
  const [reviews, setReviews] = useState([]);
  const [reviewText, setReviewText] = useState("");
  const [rating, setRating] = useState(5);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    const userData = token ? JSON.parse(atob(token.split(".")[1])) : null;
    setUser(userData);

    fetch(`http://localhost:5000/api/reviews/${productId}`)
      .then((res) => res.json())
      .then((data) => setReviews(data));
  }, [productId]);

  const handleSubmit = async () => {
    if (!user) return;

    const token = localStorage.getItem("token");
    try {
      const response = await fetch("http://localhost:5000/api/reviews", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + token,
        },
        body: JSON.stringify({ productId, reviewText, rating })
      });

      if (response.ok) {
        const updated = await response.json();
        setReviews((prev) => [...prev, updated]);
        setReviewText("");
        setRating(5);
      }
    } catch (err) {
      console.error("❌ Грешка при изпращане на ревю:", err);
    }
  };

  return (
    <div className="bg-white p-6 rounded-xl shadow">
      <h3 className="text-lg font-semibold text-center text-blue-700 mb-4">Ревюта</h3>

      {reviews.length === 0 ? (
        <p className="text-gray-500 text-center mb-4">Няма налични ревюта.</p>
      ) : (
        <div className="space-y-4 mb-6">
          {reviews.map((review, idx) => (
            <div key={idx} className="flex gap-3 items-start bg-white border border-gray-200 rounded-lg p-4 shadow hover:shadow-md transition">
              <div className="w-10 h-10 flex items-center justify-center rounded-full bg-blue-100 text-blue-700 font-semibold">
                {review.UserName?.charAt(0).toUpperCase()}
              </div>
              <div className="flex-1">
                <div className="flex justify-between items-center mb-1">
                  <span className="text-sm font-medium text-gray-800">
                    {review.UserName || "Анонимен"}
                  </span>
                  <span className="text-yellow-500 font-semibold">
                    ⭐ {review.Rating} / 5
                  </span>
                </div>
                <p className="text-gray-700 text-sm italic">
                  “{review.ReviewText}”
                </p>
              </div>
            </div>
          ))}
        </div>
      )}

      {user && (
        <div className="border-t pt-4 mt-6 bg-gray-50 p-4 rounded-lg">
          <h4 className="font-semibold mb-4 text-center text-gray-800 text-lg">Добави ревю</h4>
          <div className="space-y-3">
            <textarea
              value={reviewText}
              onChange={(e) => setReviewText(e.target.value)}
              placeholder="Какво мислиш за този продукт?"
              className="w-full border border-gray-300 rounded-lg p-3 focus:outline-none focus:ring-2 focus:ring-blue-500 resize-none min-h-[100px]"
            />
            <select
              value={rating}
              onChange={(e) => setRating(Number(e.target.value))}
              className="w-full border border-gray-300 rounded-lg p-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              {[5, 4, 3, 2, 1].map((r) => (
                <option key={r} value={r}>{r}</option>
              ))}
            </select>
            <button
              onClick={handleSubmit}
              className="w-full bg-blue-600 text-white px-4 py-2 rounded-lg font-semibold hover:bg-blue-700 transition"
            >
              Изпрати ревю
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default ReviewSection;
