import React from "react";
import { ShieldCheck, Cpu, Lock } from "lucide-react";

export default function Hero() {
  return (
    <section className="w-full bg-blue-800 text-white py-20 px-4">
      <div className="max-w-6xl mx-auto flex flex-col items-center text-center">
        <div className="mb-16">
          <h1 className="text-4xl md:text-6xl font-extrabold mb-4">
            The Best Antivirus Protection for 2025
          </h1>
          <p className="text-lg md:text-xl mb-8 max-w-2xl mx-auto">
            Comprehensive tests and reviews of the top antivirus solutions to keep your devices protected from the latest threats.
          </p>
          <a
            href="#top-picks"
            className="bg-white text-blue-700 px-6 py-3 rounded-full text-lg font-semibold hover:bg-gray-100 transition"
          >
            View Top Picks
          </a>
        </div>

        <div className="w-full grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {[
            {
              icon: <ShieldCheck size={40} className="text-blue-300" />,
              title: "Comprehensive Protection",
              desc: "We test protection against malware, ransomware, phishing, and more.",
            },
            {
              icon: <Cpu size={40} className="text-blue-300" />,
              title: "Performance Impact",
              desc: "We evaluate antivirus speed during scans and daily use.",
            },
            {
              icon: <Lock size={40} className="text-blue-300" />,
              title: "Additional Features",
              desc: "We review VPNs, password managers, parental controls, and more.",
            },
          ].map((card, i) => (
            <div
              key={i}
              className="bg-blue-900 p-6 rounded-xl shadow-md hover:shadow-lg transition duration-300 text-left flex flex-col items-start"
            >
              <div className="mb-4">{card.icon}</div>
              <h3 className="text-lg font-semibold mb-2">{card.title}</h3>
              <p className="text-sm text-blue-100">{card.desc}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
