        setProduct(data);
      })
      .catch((err) => {
        console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞—Ä–µ–∂–¥–∞–Ω–µ:", err);
        navigate("/");
      });
  }, [name, navigate]);

