import React, { useState } from "react";
import './style.css'; // ако имаш стилове

function AdminPage() {
  const [showPanel, setShowPanel] = useState(false);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");

  const handleAdd = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/products", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, description, price }),
      });

      const data = await response.json();
      alert(data.message || "Продуктът е добавен!");
      setName("");
      setDescription("");
      setPrice("");
    } catch (error) {
      console.error("Грешка при добавяне:", error);
    }
  };

  return (
    <div className="admin-container">
      {!showPanel ? (
        <button onClick={() => setShowPanel(true)} className="show-panel-btn">
          Админ Панел
        </button>
      ) : (
        <div>
          <input
            type="text"
            placeholder="Име"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="text"
            placeholder="Описание"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <input
            type="number"
            placeholder="Цена"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
          <button onClick={handleAdd}>Добави</button>
        </div>
      )}
    </div>
  );
}

export default AdminPage;
