import React, { useEffect, useState } from "react";

function ExpertReviews() {
  const [products, setProducts] = useState([]);
  const [expandedId, setExpandedId] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/admin")
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((err) => console.error("Error loading products:", err));
  }, []);

  const toggleExpand = (id) => {
    setExpandedId(expandedId === id ? null : id);
  };

  const parseLines = (text) =>
    typeof text === "string"
      ? text.split("\n").filter((line) => line.trim() !== "")
      : [];

  return (
    <section className="max-w-4xl mx-auto py-10">
      <h2 className="text-2xl font-bold text-center text-blue-800 mb-2">
        Expert Reviews
      </h2>
      <p className="text-center text-sm text-gray-600 mb-8">
        Our in-depth analysis of each antivirus solution based on extensive testing.
      </p>

      {products.map((product) => (
        <div
          key={product.ProductID}
          className="bg-white rounded shadow mb-6 overflow-hidden"
        >
          <button
            onClick={() => toggleExpand(product.ProductID)}
            className="flex items-center justify-between w-full px-6 py-4 bg-gray-100 hover:bg-gray-200"
          >
            <div className="flex items-center gap-4">
              <img
                src={product.ImageURL}
                alt={product.ProductName}
                className="w-12 h-12 object-contain rounded"
              />
              <div>
                <h3 className="font-semibold text-left">
                  {product.ProductName}
                </h3>
                <p className="text-yellow-500 text-sm">
                  ⭐ {product.AverageRating?.toFixed(1) || "N/A"}/5.0
                </p>
              </div>
            </div>
            <span className="text-lg">
              {expandedId === product.ProductID ? "▲" : "▼"}
            </span>
          </button>

          {expandedId === product.ProductID && (
            <div className="p-6 md:flex gap-6 text-sm">
              <div className="md:w-2/3">
                <h4 className="font-semibold mb-2">Our Review</h4>
                <p className="mb-4 whitespace-pre-line">
                  {product.OurReview || "No review available."}
                </p>

                <div className="flex gap-10">
                  <div>
                    <h5 className="text-green-600 font-semibold mb-1">Pros:</h5>
                    <ul className="text-green-600 list-disc ml-5">
                      {parseLines(product.Pros).map((pro, i) => (
                        <li key={i}>+ {pro}</li>
                      ))}
                    </ul>
                  </div>
                  <div>
                    <h5 className="text-red-600 font-semibold mb-1">Cons:</h5>
                    <ul className="text-red-600 list-disc ml-5">
                      {parseLines(product.Cons).map((con, i) => (
                        <li key={i}>- {con}</li>
                      ))}
                    </ul>
                  </div>
                </div>

                <h5 className="mt-5 font-semibold">Bottom Line</h5>
                <p className="mb-3 whitespace-pre-line">
                  {product.BottomLine || "Summary not available."}
                </p>
              </div>

              <div className="md:w-1/3 bg-blue-50 p-4 rounded">
                <h5 className="font-semibold mb-2">Test Results</h5>
                <div className="space-y-2 text-sm">
                  {["MalwareProtection", "PerformanceImpact", "UserInterface", "ValueForMoney"].map((metric) => (
                    <div key={metric} className="flex justify-between">
                      <span>{metric.replace(/([A-Z])/g, ' $1').trim()}</span>
                      <span className="font-semibold">
                        {product[metric] ? (
                          <>
                            <div className="w-full bg-gray-200 h-2 rounded overflow-hidden">
                              <div
                                className="bg-blue-500 h-2"
                                style={{ width: `${(product[metric] / 5) * 100}%` }}
                              ></div>
                            </div>
                            {product[metric]}/5
                          </>
                        ) : (
                          "- /5"
                        )}
                      </span>
                    </div>
                  ))}
                </div>

                <h5 className="font-semibold mt-4 mb-1">Protection Features</h5>
                <ul className="text-sm space-y-1">
                  {parseLines(product.ProtectionFeatures).map((feature, i) => (
                    <li key={i} className="flex items-center gap-2">
                      <span>◯</span>
                      <span>{feature}</span>
                    </li>
                  ))}
                </ul>

                <button
                  onClick={() => window.open(product.OfficialWebsite, "_blank")}
                  className="mt-4 w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
                >
                  Visit Official Website
                </button>
              </div>
            </div>
          )}
        </div>
      ))}
    </section>
  );
}

export default ExpertReviews;
