import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./AntivirusList.css";
function AntivirusListT() {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:5000/api/products")
      .then((res) => res.json())
      .then((data) => {
        console.log("üîÑ –ó–∞—Ä–µ–¥–µ–Ω–∏ –ø—Ä–æ–¥—É–∫—Ç–∏: ", data);
        setProducts(data);
      })
      .catch((err) => console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞—Ä–µ–∂–¥–∞–Ω–µ:", err));
  }, []);

  return (
    <section className="p-8">
      <h2 className="text-2xl font-bold mb-6 text-center">–ù–∞—à–∏—Ç–µ –ê–Ω—Ç–∏–≤–∏—Ä—É—Å–Ω–∏ –ü—Ä–µ–¥–ª–æ–∂–µ–Ω–∏—è</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {products.map((product) => (
          <div key={product.ProductID} className="bg-white p-4 rounded-lg shadow-md text-center">
            <img src={product.ImageURL} alt={product.ProductName} className="h-20 mx-auto mb-4" />
            <h3 className="font-semibold">{product.ProductName}</h3>
            <p className="text-sm text-gray-600 mb-2">{product.Description}</p>
            <p className="text-blue-700 font-bold">{product.Price}</p>
            <button
              className="mt-2 bg-blue-600 text-white px-4 py-1 rounded"
              onClick={() => navigate(`/product/${encodeURIComponent(product.ProductName)}`)}
            >
              –†–µ–≤—é
            </button>
          </div>
        ))}
      </div>
    </section>
  );
}

export default AntivirusListT;
