import React, { useEffect, useState } from "react";
import "./index.css";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Login from "./Login";
import Register from "./Register";
import AdminPage from "./AdminPageT";
import ProtectedRoute from "./ProtectedRoute";
import Navbar from "./components/Navbar";
import Hero from "./components/Hero";
import AntivirusList from "./components/AntivirusListT";
import ProductDetails from "./ProductDetails";

function App() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Автоматично отписване при всяко ново зареждане
    localStorage.removeItem("token");
    localStorage.removeItem("isAdmin");

    // Зареждаме продуктите от API
    fetch("http://localhost:5000/api/products")
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((err) => console.error("Грешка при зареждане на продукти:", err));
  }, []);

  return (
    <div className="min-h-screen">
      <Router>
        <Routes>
          {/* Начална страница */}
          <Route
            path="/"
            element={
              <>
                <Navbar />
                <Hero />
                <AntivirusList products={products} />
              </>
            }
          />

          {/* Login */}
          <Route path="/login" element={<Login />} />

          {/* Register */}
          <Route path="/register" element={<Register />} />

          {/* Admin панел */}
          <Route
            path="/admin"
            element={
              <ProtectedRoute>
                <AdminPage />
              </ProtectedRoute>
            }
          />

          {/* Подробности за продукт */}
          <Route path="/product/:id" element={<ProductDetails />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
