import React, { useEffect, useState } from "react";
import "./index.css";

function AdminPage() {
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({ name: "", description: "", price: "", image: "" });
  const [fileName, setFileName] = useState("");
  const [editing, setEditing] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const res = await fetch("http://localhost:5000/api/admin");
    const data = await res.json();
    setProducts(data);
  };

  const convertToBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });
  };

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const base64 = await convertToBase64(file);
      setForm({ ...form, image: base64 });
      setFileName(file.name);
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const method = editing ? "PUT" : "POST";
    const url = editing
      ? `http://localhost:5000/api/admin/${editing.ProductID}`
      : "http://localhost:5000/api/admin";

    const res = await fetch(url, {
      method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });

    if (res.ok) {
      setForm({ name: "", description: "", price: "", image: "" });
      setFileName("");
      setEditing(null);
      fetchProducts();
    }
  };

  const handleEdit = (product) => {
    setForm({
      name: product.ProductName,
      description: product.Description,
      price: product.Price,
      image: product.ImageURL,
    });
    setEditing(product);
    setFileName("(existing image)");
  };

  const handleDelete = async (id) => {
    await fetch(`http://localhost:5000/api/admin/${id}`, { method: "DELETE" });
    fetchProducts();
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-600 via-pink-500 to-red-500 p-6">
      <div className="max-w-xl mx-auto bg-white rounded-2xl shadow-xl p-6 animate-fade-in">
        <h2 className="text-2xl font-bold text-center text-gray-800 mb-4">
          {editing ? "Edit Product" : "Add New Product"}
        </h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="text"
            name="name"
            placeholder="Product Name"
            value={form.name}
            onChange={handleChange}
            required
            className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500"
          />
          <input
            type="text"
            name="description"
            placeholder="Description"
            value={form.description}
            onChange={handleChange}
            required
            className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500"
          />
          <input
            type="text"
            name="price"
            placeholder="Price"
            value={form.price}
            onChange={handleChange}
            required
            className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500"
          />
          <label className="custom-file-upload block w-full">
            <input
              type="file"
              accept="image/*"
              onChange={handleImageChange}
              className="w-full"
            />
          </label>
          {fileName && (
            <p className="file-name-preview text-green-600 text-sm">📷 {fileName}</p>
          )}
          <button
            type="submit"
            className="w-full bg-blue-600 text-white font-semibold py-2 rounded-lg hover:bg-blue-700 transition duration-300"
          >
            {editing ? "Update Product" : "Add Product"}
          </button>
        </form>
      </div>

      <div className="mt-10 max-w-6xl mx-auto bg-white rounded-2xl shadow-xl overflow-x-auto animate-fade-in">
        <table className="min-w-full text-sm text-gray-800">
          <thead>
            <tr className="bg-gray-100 text-left">
              <th className="py-3 px-5">Image</th>
              <th className="py-3 px-5">Name</th>
              <th className="py-3 px-5">Description</th>
              <th className="py-3 px-5">Price</th>
              <th className="py-3 px-5">Actions</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product) => (
              <tr key={product.ProductID} className="border-t hover:bg-gray-50 transition">
                <td className="py-3 px-5">
                  <img
                    src={product.ImageURL}
                    alt={product.ProductName}
                    className="w-12 h-12 object-contain rounded"
                  />
                </td>
                <td className="py-3 px-5 font-semibold">{product.ProductName}</td>
                <td className="py-3 px-5">{product.Description}</td>
                <td className="py-3 px-5">{product.Price}</td>
                <td className="py-3 px-5 flex gap-2">
                  <button
                    onClick={() => handleEdit(product)}
                    className="edit-button"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(product.ProductID)}
                    className="delete-button"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
            {products.length === 0 && (
              <tr>
                <td colSpan="5" className="py-5 text-center text-gray-400">
                  No products found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AdminPage;
