import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function AntivirusListT() {
  const [products, setProducts] = useState([]);
  const [sortBy, setSortBy] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:5000/api/admin")
      .then((res) => res.json())
      .then((data) => {
        console.log("üîÑ –ó–∞—Ä–µ–¥–µ–Ω–∏ –ø—Ä–æ–¥—É–∫—Ç–∏: ", data);
        setProducts(Array.isArray(data) ? data : []);
      })
      .catch((err) => console.error("‚ùå –ì—Ä–µ—à–∫–∞ –ø—Ä–∏ –∑–∞—Ä–µ–∂–¥–∞–Ω–µ:", err));
  }, []);

  const sortedProducts = [...products];
  if (sortBy === "price") {
    sortedProducts.sort((a, b) => a.Price - b.Price);
  } else if (sortBy === "rating") {
    sortedProducts.sort((a, b) => (b.Rating || 0) - (a.Rating || 0));
  }

  // –ù–∞–π-–≤–∏—Å–æ–∫ —Ä–µ–π—Ç–∏–Ω–≥
  const topRated = [...products].sort((a, b) => (b.Rating || 0) - (a.Rating || 0))[0]?.ProductID;

  // –ù–∞–π-–Ω–∏—Å–∫–∞ —Ü–µ–Ω–∞
  const bestValue = [...products].sort((a, b) => a.Price - b.Price)[0]?.ProductID;

  return (
    <section className="p-8 bg-gray-100 min-h-screen">
      <h2 className="text-2xl font-bold mb-4 text-center">–ù–∞—à–∏—Ç–µ –ê–Ω—Ç–∏–≤–∏—Ä—É—Å–Ω–∏ –ü—Ä–µ–¥–ª–æ–∂–µ–Ω–∏—è</h2>

      {/* –°–æ—Ä—Ç–∏—Ä–∞–Ω–µ */}
      <div className="flex justify-center mb-6">
        <label className="mr-2 font-medium">–°–æ—Ä—Ç–∏—Ä–∞–π –ø–æ:</label>
        <select
          className="border px-3 py-1 rounded"
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
        >
          <option value="">-- –ë–µ–∑ —Å–æ—Ä—Ç–∏—Ä–∞–Ω–µ --</option>
          <option value="price">–¶–µ–Ω–∞</option>
          <option value="rating">–†–µ–π—Ç–∏–Ω–≥</option>
        </select>
      </div>

      {/* –ö–∞—Ä—Ç–∏ */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {sortedProducts.map((product) => (
          <div
            key={product.ProductID}
            className="relative bg-white p-4 rounded-lg shadow-md text-center"
          >
            {/* –õ–µ–Ω—Ç–∞: Editor / Value */}
            {product.ProductID === topRated && (
              <div className="absolute top-0 left-0 w-full bg-blue-600 text-white py-1 rounded-t-lg text-sm font-semibold">
                üèÖ Editor's Choice
              </div>
            )}
            {product.ProductID === bestValue && product.ProductID !== topRated && (
              <div className="absolute top-0 left-0 w-full bg-green-600 text-white py-1 rounded-t-lg text-sm font-semibold">
                üí∞ Best Value
              </div>
            )}

            <img
              src={product.ImageURL}
              alt={product.ProductName}
              className="h-20 mx-auto my-6"
            />
            <h3 className="font-semibold">{product.ProductName}</h3>
            <p className="text-sm text-gray-600 mb-2">{product.Description}</p>
            <p className="text-blue-700 font-bold">{product.Price} –ª–≤.</p>

            {product.Rating && (
              <p className="text-yellow-500 mt-1 font-medium">
                ‚≠ê {product.Rating.toFixed(1)} / 5.0
              </p>
            )}

            <div className="flex justify-center gap-2 mt-4">
              <button
                className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700"
                onClick={() => window.open("https://www.example.com", "_blank")}
              >
                Visit Site
              </button>
              <button
                className="bg-gray-200 text-blue-700 px-3 py-1 rounded hover:bg-gray-300"
                onClick={() =>
                  navigate(`/product/${encodeURIComponent(product.ProductName)}`)
                }
              >
                –ü—Ä–æ—á–µ—Ç–∏ –†–µ–≤—é
              </button>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}

export default AntivirusListT;
