import React, { useEffect, useState } from "react";
import Navbar from "./components/Navbar";
import "./index.css";

export default function AdminPageT() {
  const [products, setProducts] = useState([]);
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [image, setImage] = useState("");

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin/products");
      const data = await res.json();
      setProducts(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("Error loading products:", err);
    }
  };

  const handleAdd = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/admin/products", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, description, price, image }),
      });
      await res.json();
      fetchProducts();
      setName("");
      setDescription("");
      setPrice("");
      setImage("");
    } catch (err) {
      console.error("Error adding product:", err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await fetch(`http://localhost:5000/api/admin/products/${id}`, {
        method: "DELETE",
      });
      fetchProducts();
    } catch (err) {
      console.error("Error deleting product:", err);
    }
  };

  return (
    <>
      <Navbar />
      <div className="min-h-screen bg-gradient-to-br from-purple-400 via-pink-400 to-pink-600 py-16 px-4 flex flex-col items-center">
        <div className="bg-white shadow-lg rounded-2xl p-8 w-full max-w-md mb-12">
          <h2 className="text-xl font-semibold text-center text-gray-800 mb-6">Add New Product</h2>
          <input
            className="w-full mb-4 px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            className="w-full mb-4 px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <input
            className="w-full mb-4 px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="Price"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
          <input
            className="w-full mb-4 px-4 py-2 border rounded-lg focus:outline-none"
            placeholder="Image URL (Base64 or Link)"
            value={image}
            onChange={(e) => setImage(e.target.value)}
          />
          <button
            className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 transition"
            onClick={handleAdd}
          >
            Add
          </button>
        </div>

        <div className="bg-white shadow-md rounded-2xl overflow-x-auto w-full max-w-4xl">
          <table className="min-w-full text-sm text-gray-800">
            <thead className="bg-gray-100">
              <tr>
                <th className="px-4 py-3 text-left">Image</th>
                <th className="px-4 py-3 text-left">Name</th>
                <th className="px-4 py-3 text-left">Description</th>
                <th className="px-4 py-3 text-left">Price</th>
                <th className="px-4 py-3 text-left">Actions</th>
              </tr>
            </thead>
            <tbody>
              {products.map((product) => (
                <tr key={product.ProductID} className="border-t">
                  <td className="px-4 py-2">
                    {product.ImageURL && (
                      <img src={product.ImageURL} alt="Product" className="w-12 h-auto rounded" />
                    )}
                  </td>
                  <td className="px-4 py-2">{product.ProductName}</td>
                  <td className="px-4 py-2">{product.Description}</td>
                  <td className="px-4 py-2">{product.Price}</td>
                  <td className="px-4 py-2 flex gap-2">
                    <button className="bg-yellow-400 hover:bg-yellow-500 text-white px-3 py-1 rounded-lg">Edit</button>
                    <button
                      className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded-lg"
                      onClick={() => handleDelete(product.ProductID)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
              {products.length === 0 && (
                <tr>
                  <td className="px-4 py-4 text-center text-gray-500" colSpan="5">
                    No products found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
}
