import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

function ComparePage() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/admin") // правилният маршрут за продукти
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((err) => console.error("❌ Грешка при зареждане на продукти:", err));
  }, []);

  return (
    <div className="p-8 bg-gray-100 min-h-screen">
      {/* Начало бутон */}
      <Link
        to="/"
        className="inline-block mb-4 px-4 py-2 text-black-600 rounded  transition font-medium"
      >
        Начало
      </Link>

      <h2 className="text-3xl font-semibold mb-6">Сравнение на Антивирусни Програми</h2>

      <table className="w-full border-collapse bg-white shadow-sm">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-4 border">Име</th>
            <th className="p-4 border">Цена</th>
            <th className="p-4 border">Рейтинг</th>
            <th className="p-4 border">Описание</th>
          </tr>
        </thead>
        <tbody>
          {products.map((p) => (
            <tr key={p.ProductID} className="hover:bg-gray-100">
              <td className="p-4 border">{p.ProductName}</td>
              <td className="p-4 border">{p.Price} лв.</td>
              <td className="p-4 border">{p.Rating || "Няма"} ⭐</td>
              <td className="p-4 border">{p.Description}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ComparePage;
